import React, { Component } from 'react'

class UpdateUserForm extends Component {
  constructor(props){
    super(props);
    this.state = {
      showForm: false,
      newUser :{
        id: 'this.props.data.id',
        name: 'this.props.data.name',
        username: 'this.props.data.username',
        email: 'this.props.data.email',
        address: {
          street: 'this.props.data.adddress.street',
          suite: 'this.props.data.adddress.',
          city: 'this.props.data.adddress.',
          zipcode: 'this.props.data.adddress.',
          geo: {
            lat: 'this.props.data.',
            lng: 'this.props.data.',
          }
        },
        phone: 'this.props.data.',
        website: 'this.props.data.',
        company: {
          cname: 'this.props.data.',
          catchPhrase: 'this.props.data.',
          bs: 'this.props.data.',
        }
      },
    };

  this.onClick = this.onClick.bind(this);
  }

  onClick () {  this.setState({ showForm: true }); }
  renderForm(){
    const lat = (this.state.newUser.address.geo ? this.state.newUser.address.geo.lat:'')      
    const lng = (this.state.newUser.address.geo ? this.state.newUser.address.geo.lng:'')

    return (
        <div> 
            <form id= "add-app" onSubmit ={this.handleSubmit}>
              <h2>form</h2>
                <label>Name: </label>
                <input type="text" placeholder ='name'
                  name = 'name'
                  value = { this.state.name}
                  onChange ={ this.handleChange}
                  required
                />

                <label>User Name : </label>
                <input type="text" placeholder ='username'
                  name ='username'
                  value = { this.state.username}
                  onChange ={ this.handleChange}
                />

                <label>Email : </label>
                <input type="email" placeholder ='email'
                  name = 'email'
                  value = { this.state.email }
                  onChange ={ this.handleChange}
                />

                 <h4>Address</h4>
                <label>Street: </label>
                <input type="text" placeholder ='input'
                  name = 'street'
                  value = { this.state.newUser.address.street || ''}
                  onChange = { this.handleChange}
                />

                <label>Suite: </label>
                <input type="text" 
                  name = 'suite'
                  value = { this.state.newUser.address.suite}
                  onChange ={ this.handleChange}
                />

                <label>City </label>
                <input type="text" 
                  name = 'city'
                  value = { this.state.newUser.address.city}
                  onChange = { this.handleChange}
                />

                <label>Zipcode: </label>
                <input type="number" placeholder ='zipcode'
                  name = 'zipcode'
                  value = { this.state.newUser.address.zipcode}
                  onChange = { this.handleChange}
                /><br/>

                <label>Latitude: </label>
                <input type="number" placeholder ='latitude'
                  name = 'lat'
                  value = {lat}
                  onChange = { this.handleChange}
                />

                <label>Longitude: </label>
                <input type="number" placeholder ='longitude'
                  name = 'lng'
                  value = {lng}
                  onChange = { this.handleChange}
                />

                <label>Phone: </label>
                <input type="number" placeholder ='phone number'
                  name = 'phone'
                  value = { this.state.phone}
                  onChange = { this.handleChange}
                />

                <label>Website: </label>
                <input type="text" placeholder ='input'
                  name = 'website'
                  value = { this.state.website}
                  onChange = { this.handleChange}
                />

                <h4>Company</h4>
                <label>Company Name: </label>
                <input type="text" placeholder ='input'
                  name = 'cname'
                  value = { this.state.newUser.company.cname}
                  onChange = { this.handleChange}
                />

                <label>CatchPhrase: </label>
                <input type="text" placeholder ='input'
                  name = 'catchPhrase'
                  value = { this.state.newUser.company.catchPhrase}
                  onChange = { this.handleChange}
                />

                <label>Bs: </label>
                <input type="text" placeholder ='input'
                  name = 'bs'
                  value = { this.state.newUser.company.bs}
                  onChange = { this.handleChange}
                />
                <button>Create</button>
           </form>
       </div>
    );
  }
  render() {
    const { showForm } = this.state;
    return (
      <div>
        <button onClick={ this.onClick } >EDIT</button>
        { showForm && this.renderForm() }
      </div>
    )
  }
}

export default UpdateUserForm
