      .then(response => {
        const todos = response.data
        console.log()
        dispatch(getAllTodoSuccess(todos))
      })
      .catch(error => {
        dispatch(getAllTodoFailure(error.message))
      })
  }
}

export const getAllTodoRequest = () => {
  return {
    type: GETALL_TODO_REQUEST
  }
}

export const getAllTodoSuccess = todos => {
