import React, { Component } from 'react'
import { connect } from 'react-redux'
import { fetchUser,deleteUser } from '../actions/index'
import UserContainer from '../containers/UserContainer';

class User extends Component {
  constructor(props){
    super(props);
  }

  componentDidMount() {
    this.props.fetchUser();
  }

  handleEdit(users){
    console.log(users)
    history.pushState({
      pathname:`/edit/${users.id}`
    })
  }

  handleDelete(users){ 
    console.log(users)
    this.props.deleteUser(users);
  }

  render() {
    if(this.props.isLoding){
      return ( <p>Loading....</p>)
    }
    else if(this.props.error){
      return(
        <div role ='alert'>
          {this.props.error.message}
        </div>
      )
    }
    else {
      return (
        <div>
          <UserContainer
            updateUser = {this.handleEdit.bind(this)}
            deleteUser = {this.handleDelete.bind(this)}
          />
        </div>
      )
    }
  }
}

export default connect(null , { fetchUser ,deleteUser })(User)
