import React, { Component } from 'react';
import {connect} from 'react-redux'
import { addUser } from '../actions/index'

class AddUser extends Component {
  constructor(props){
    super(props);
    this.state = {
      showForm: false,
      newUser :{
        id: '',
        name: '',
        username: '',
        email: '',
        address: {
          street: '',
          suite: '',
          city: '',
          zipcode: '',
          geo: {
            lat: '',
            lng: '',
          }
        },
        phone: '',
        website: '',
        company: {
          name: '',
          catchPhrase: '',
          bs: '',
        }
      }
    };

  this.onClick = this.onClick.bind(this);
  }

  onClick () {
    // On click we change our state â€“ this will trigger our `render` method
    this.setState({ showForm: true });
}

handleChange = (e) =>{
  this.setState({[e.target.name]: e.target.value});
}

handleSubmit = (e) => {
  e.preventDefault();
  this.props.addUser(this.state.newUser)
}

renderForm () {
  return (
      <div> 
          <form id= "add-app" onSubmit ={this.handleSubmit}>
            <h2>form</h2>
              <label>User id : </label>
              <input type="number" placeholder ='id'
                value = { this.state.id}
                name = 'id'
                onChange ={ this.handleChange}
                required
              />

              <label>Name: </label>
              <input type="text" placeholder ='name'
                name = 'name'
                value = { this.state.name}
                onChange ={ this.handleChange}
                required
              />

              <label>User Name : </label>
              <input type="text" placeholder ='username'
                name ='username'
                value = { this.state.username}
                onChange ={ this.handleChange}
              />

              <label>Email : </label>
              <input type="email" placeholder ='email'
                name = 'email'
                value = { this.state.email}
                onChange ={ this.handleChange}
              />

               <h4>Address</h4>
              <label>Street: </label>
              <input type="text" placeholder ='input'
                name = 'street'
                value = { this.state.street}
                onChange = { this.handleChange}
              />

              <label>Suite: </label>
              <input type="text" 
                name = 'suite'
                value = { this.state.suite}
                onChange ={ this.handleChange}
              />

              <label>City </label>
              <input type="text" 
                name = 'city'
                value = { this.state.city}
                onChange = { this.handleChange}
              />

              <label>Zipcode: </label>
              <input type="number" placeholder ='zipcode'
                name = 'zipcode'
                value = { this.state.zipcode}
                onChange = { this.handleChange}
              /><br/>

              <label>Latitude: </label>
              <input type="number" placeholder ='latitude'
                name = 'latitude'
                value = { this.state.latitude}
                onChange = { this.handleChange}
              />

              <label>Longitude: </label>
              <input type="number" placeholder ='longitude'
                name = 'longitude'
                value = { this.state.longitude}
                onChange = { this.handleChange}
              />

              <label>Phone: </label>
              <input type="number" placeholder ='phone number'
                name = 'phone'
                value = { this.state.phone}
                onChange = { this.handleChange}
              />

              <label>Website: </label>
              <input type="text" placeholder ='input'
                name = 'website'
                value = { this.state.website}
                onChange = { this.handleChange}
              />

              <h4>Company</h4>
              <label>Company Name: </label>
              <input type="text" placeholder ='input'
                name = 'companyname'
                value = { this.state.companyname}
                onChange = { this.handleChange}
              />

              <label>CatchPhrase: </label>
              <input type="text" placeholder ='input'
                name = 'catchPhrase'
                value = { this.state.catchPhrase}
                onChange = { this.handleChange}
              />

              <label>Bs: </label>
              <input type="text" placeholder ='input'
                name = 'bs'
                value = { this.state.bs}
                onChange = { this.handleChange}
              />
              <button>Create</button>
         </form>
     </div>
  );
}
  render() {
    const { showForm } = this.state;
    return (
      <div>
          <button onClick={ this.onClick }>Add User</button>
           {/* We want to show the form if the state is true */}
           {showForm && this.renderForm()}
      </div>
    )
  }
}

export default connect(null , { addUser})( AddUser);
