import React, { Component } from 'react'

class UpdateUserForm extends Component {

  render() {
    const { showForm } = this.state;
    return (
      <div>
        <button onClick={ this.onClick } >EDIT</button>
        { showForm && this.renderForm() }
      </div>
    )
  }
}

export default UpdateUserForm
