import React, { Component } from 'react';
import {connect} from 'react-redux'
import { addUser ,fetchUser } from '../actions/index'

class AddUser extends Component {
  constructor(props){
    super(props);
    this.state = {
      showForm: false,
      newUser :{
        id: '',
        name: '',
        username: '',
        email: '',
        address: {
          street: '',
          suite: '',
          city: '',
          zipcode: '',
          geo: {
            lat: '',
            lng: '',
          }
        },
        phone: '',
        website: '',
        company: {
          name: '',
          catchPhrase: '',
          bs: '',
        }
      }
    };

  this.onClick = this.onClick.bind(this);
  }

  onClick () {
    // On click we change our state â€“ this will trigger our `render` method
    this.setState({ showForm: true });
}

handleChange = (e) =>{
  // this.setState({[e.target.name]: e.target.value});
  if (e.target.name === 'name') {
    this.setState({newUser :{...this.state.newUser ,name: e.target.value} });

  } else if (e.target.name === 'username') {
    this.setState({ newUser :{...this.state.newUser ,username: e.target.value} });

  } else if (e.target.name === 'email') {
    this.setState({ newUser :{...this.state.newUser ,email: e.target.value }});

  } else if (e.target.name === 'street') {
    this.setState({
      newUser :{
        ...this.state.newUser ,address:{ ...this.state.address,
          street: e.target.value }}
    });

  } else if (e.target.name === 'suite') {
    this.setState({
      newUser :{
        ...this.state.newUser ,address:{ ...this.state.address
         ,suite: e.target.value }}
    });

  } else if (e.target.name === 'city') {
    this.setState({
      newUser :{
        ...this.state.newUser ,address:{  ...this.state.address, city: e.target.value} }
    });

  } else if (e.target.name === 'zipcode') {
    this.setState({
      newUser :{
        ...this.state.newUser,address:{...this.state.address ,zipcode: e.target.value }}
    });

  } else if (e.target.name === 'lat') {
    this.setState({
      newUser :{
        ...this.state.newUser,
          ...this.state.address,
            ...this.state.geo,
              lat: e.target.value }
    });

  } else if (e.target.name === 'lng') {
    this.setState({ 
      newUser :{
      ...this.state.newUser,
        ...this.state.address,
          ...this.state.geo,
            lng: e.target.value
      }
    });

  } else if (e.target.name === 'phone') {
    this.setState({  newUser :{...this.state.newUser ,phone: e.target.value }});

  } else if (e.target.name === 'website') {
    this.setState({ newUser :{...this.state.newUser ,website: e.target.value }});

  } else if (e.target.name === 'name') {
    this.setState({
      newUser :{
        ...this.state.newUser, ...this.state.company ,name: e.target.value }
    });

  } else if (e.target.name === 'catchPhrase') {
    this.setState({
      newUser :{
        ...this.state.newUser, 
          ...this.state.company ,
            catchPhrase: e.target.value }
    });

  } else if (e.target.name === 'bs') {
    this.setState({
      newUser :{
        ...this.state.newUser,
          ...this.state.company ,
            bs: e.target.value
          }
    });
  }
}

handleSubmit = async (e) => {
  e.preventDefault();
  //console.log('hello',this.state.newUser)
  await this.props.addUser(this.state.newUser)
  await this.props.fetchUser();
}

renderForm(){
  const lat = (this.state.newUser.address.geo ? this.state.newUser.address.geo.lat:'')      
  const lng = (this.state.newUser.address.geo ? this.state.newUser.address.geo.lng:'')
  return (
      <div> 
          <form id= "add-app" onSubmit ={this.handleSubmit}>
            <h2>form</h2>
              <label>Name: </label>
              <input type="text" placeholder ='name'
                name = 'name'
                value = { this.state.name}
                onChange ={ this.handleChange}
                required
              />

              <label>User Name : </label>
              <input type="text" placeholder ='username'
                name ='username'
                value = { this.state.username}
                onChange ={ this.handleChange}
              />

              <label>Email : </label>
              <input type="email" placeholder ='email'
                name = 'email'
                value = { this.state.email}
                onChange ={ this.handleChange}
              />

               <h4>Address</h4>
              <label>Street: </label>
              <input type="text" placeholder ='input'
                name = 'street'
                value = { this.state.newUser.address.street}
                onChange = { this.handleChange}
              />

              <label>Suite: </label>
              <input type="text" 
                name = 'suite'
                value = { this.state.newUser.address.suite}
                onChange ={ this.handleChange}
              />

              <label>City </label>
              <input type="text" 
                name = 'city'
                value = { this.state.newUser.address.city}
                onChange = { this.handleChange}
              />

              <label>Zipcode: </label>
              <input type="number" placeholder ='zipcode'
                name = 'zipcode'
                value = { this.state.newUser.address.zipcode}
                onChange = { this.handleChange}
              /><br/>

              <label>Latitude: </label>
              <input type="number" placeholder ='latitude'
                name = 'lat'
                value = {lat}
                onChange = { this.handleChange}
              />

              <label>Longitude: </label>
              <input type="number" placeholder ='longitude'
                name = 'lng'
                value = {lng}
                onChange = { this.handleChange}
              />

              <label>Phone: </label>
              <input type="number" placeholder ='phone number'
                name = 'phone'
                value = { this.state.phone}
                onChange = { this.handleChange}
              />

              <label>Website: </label>
              <input type="text" placeholder ='input'
                name = 'website'
                value = { this.state.website}
                onChange = { this.handleChange}
              />

              <h4>Company</h4>
              <label>Company Name: </label>
              <input type="text" placeholder ='input'
                name = 'companyname'
                value = { this.state.newUser.company.name}
                onChange = { this.handleChange}
              />

              <label>CatchPhrase: </label>
              <input type="text" placeholder ='input'
                name = 'catchPhrase'
                value = { this.state.newUser.company.catchPhrase}
                onChange = { this.handleChange}
              />

              <label>Bs: </label>
              <input type="text" placeholder ='input'
                name = 'bs'
                value = { this.state.newUser.company.bs}
                onChange = { this.handleChange}
              />
              <button>Create</button>
         </form>
     </div>
  );
}
  render() {
    const { showForm } = this.state;
    return (
      <div>
          <button onClick={ this.onClick }>Add User</button>
           {/* We want to show the form if the state is true */}
           { showForm && this.renderForm() }
      </div>
    )
  }
}

export default connect(null , { addUser , fetchUser})( AddUser);
